   // 2
   @SuppressWarnings({ "unchecked", "deprecation" })
   @Test
   public void testgetOperations ()
   {

      // BEgin
      Session connect = HibernateUtil.getSessionFactory ().openSession ();
      String jsonReponse;
      WebResource webResource = resource ();
      MultivaluedMap formData = new MultivaluedMapImpl ();
      String nameSMD = "TestOperationSMD2";
      createTester (nameSMD);
      SiteSMD tester = SiteSMD.findByName (connect, nameSMD);

      // First
      formData.add ("nameSMD", nameSMD);
      jsonReponse = webResource.path ("operations/getOperations/").post (String.class, formData);
      Assert.assertTrue (jsonReponse.length () > 0);

      Gson gson = new GsonBuilder ().excludeFieldsWithoutExposeAnnotation ().serializeNulls ().setPrettyPrinting ().serializeNulls ().create ();
      String result = "";
      List<OperationAbstract> lst = new ArrayList<OperationAbstract> ();
      for (int i = 0; i < tester.getOperations ().size (); i++) {
         if (!tester.getOperations ().get (i).isNested () && !tester.getOperations ().get (i).isAchieved ()) {
            lst.add (tester.getOperations ().get (i));
         }
      }

      result = gson.toJson (lst);
      Assert.assertEquals ("2: Operations ", "{\"operations\": " + result + "}", jsonReponse);

      Assert.assertEquals ("2: Nbr d'operations ", 7, lst.size ());
      connect.close ();
      deleteTester (nameSMD);
   }

